import React from 'react'
import { useParams } from 'react-router-dom'
import Head from './Head';
import styles from './Produto.module.css'
import useFech from './useFetch';

const Produto = () => {
  const { id } = useParams();
  const { fetchData, data, loading, error } = useFech();

  console.log(id)
  React.useEffect(() => {
    async function requestData() {
      const { response, json } = await fetchData(`https://ranekapi.origamid.dev/json/api/produto/${id}`)
    }
    requestData()
  }, [])

  console.log(data)

  if (error) return <div>Error: {error.message}</div>
  if (loading) return <div>Loading...</div>
  return (
    <div>
      <Head />
      {data && 
        <div className={`${styles.produto}`} key={data.id}>
          <img src={data.fotos[0].src} alt={data.fotos[0].titulo}/>
          <div>
            <h1>{data.nome}</h1>
            <p>{data.preco}</p>
          </div>
        </div>
      }
    </div>
  )
}

export default Produto
